import { Component } from '@angular/core';

@Component({
  selector: 'app-ng-switch-case',
  templateUrl: './ng-switch-case.component.html',
  styleUrls: ['./ng-switch-case.component.css']
})
export class NgSwitchCaseComponent {

}
<h2>ng Switch Case</h2>

<div [ngSwitch]="numero">
  <p *ngSwitchCase="1">a variável esta com o valor de {{numero}}</p>
  <p *ngSwitchCase="2">a variável esta com o valor de {{numero}}</p>
  <p *ngSwitchCase="3">a variável esta com o valor de {{numero}}</p>
  <p *ngSwitchCase="4">a variável esta com o valor de {{numero}}</p>
  <p *ngSwitchCase="5">a variável esta com o valor de {{numero}}</p>
  <p *ngSwitchDefault>Mensagem padrão</p>
</div>

<button (click)="incremetarNumero()">Incremeta número</button>
