import { Component } from '@angular/core';

@Component({
  selector: 'app-ng-style',
  templateUrl: './ng-style.component.html',
  styleUrls: ['./ng-style.component.css']
})
export class NgStyleComponent {

  validaFonte:boolean = false;
  validaCor: boolean = false;

  tamanho: number = 20;


  valorFonte: string = 20 + 'px' ;

  incrementar() {
    this.tamanho++;
    this.valorFonte = this.tamanho + 'px';
  }


  mudaFonte() {
    this.validaFonte = !this.validaFonte;
  }

}
<h2>[nsStyle]</h2>

<p [style.font-size]="30 + 'px'">valor fixo</p>

<p [style.font-size]="valorFonte">método da classe componente sendo referenciado no template</p>

<button (click)="incrementar()">aumentar</button>

<p [style.font-size]="validaFonte ? 'x-large' : 'smaller'"
   [style.color]="validarCor ? 'red' : 'blue'">muda valor com condição ternária</p>

   <button (click)="mudarFonte()">mudaFonte</button>
   <button (click)="mudarCor()">mudaCor</button>
