import { Component } from '@angular/core';

@Component({
  selector: 'app-ng-if',
  templateUrl: './ng-if.component.html',
  styleUrls: ['./ng-if.component.css']
})
export class NgIfComponent {

  mostraNome!: boolean;
  cursos: string [] = [];
  mostrar(): void {
    this.mostraNome = !this.mostraNome
  }
  getValor(): boolean {
    return this.mostraNome;
  }
  addCurso() {
     this.cursos.push('Angular 16')
  }




}
<h2>*ngIf</h2>

<div *ngIf="true">
  *ngIf verdadeiro
</div>


<div *ngIf="false">
  *ngIf falso
</div>


<button (click)="mostrar()">Exibir o nome da escola de treinamento</button>
<br>
<p *ngIf="getValor()">treinamento com a condicional *ngIf</p>

<p *ngIf="cursos.length == 0 "> Não temos curso disponível</p>
<p *ngIf="cursos.length  > 0 "> Agora temos curso cadastrado</p>
<button (click)="addCurso()"> add novo curso</button>
