import { Component } from '@angular/core';

@Component({
  selector: 'app-ng-for',
  templateUrl: './ng-for.component.html',
  styleUrls: ['./ng-for.component.css']
})
export class NgForComponent {

  nomes: any [] = [
    { id:1, nome:'Renata'},
    { id:2, nome:'Malcolm'},
    { id:3, nome:'Maria Luiza'},
    { id:4, nome:'José Antônio'},
    { id:5, nome:'Elton Luiz'}];

    meuSave(index: number, nomes: any) {
      return nomes.id;
    }
}
<h2>*ngFor</h2>

<ul *ngFor="let nome of nomes, let i=index; trackBy:meuSave">
  <li>nome da pessoa é {{nome}}, com id {{nome.id}},
      que está na posíção {{i + 1}}</li>
</ul>
