import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Livro Angular';

  foto: string = 'favicon.ico';

}
<h1>
  {{title}}
</h1>

<img [src]="foto">

<app-lista-pessoa></app-lista-pessoa>
